import Product from "./components/Product/Product";
import React, { Component, useState } from "react";
import Counter from "./components/Counter/Counter";
import HooksArray from "./components/Hook/HookArray";
import ProductList from "./components/ProductList/ProductList";
import Navbar from "./components/Navbar/Navbar";
import ClassCounter from "./components/Counter/ClassCounter"
import Wrapper from "./components/hoc/Wrapper";
import ClassRef from "./components/Ref/ClassRef";
import TotalPrice from "./components/TotalPrice/TotalPrice";

//context
export const ProductState = React.createContext()

class App extends Component {

  state = {

    productData: [
      { name: 'ساعت هوشمند', price: 1500e3, quantity: 1, id: 1 },
      { name: 'کتاب فروشندگی', price: 500e3, quantity: 1, id: 2 },
      { name: 'شلوار مردانه', price: 150e3, quantity: 1, id: 3 },
      { name: 'موبایل اپل', price: 25e6, quantity: 1, id: 4 },
    ]

  }

  removeHandler = (id) => {
    const filterProduct = this.state.productData.filter(p => p.id !== id)
    this.setState({ productData: filterProduct })
  }

  incrementHandler = (id) => {
    const index = this.state.productData.findIndex(p => p.id === id)

    const product = { ...this.state.productData[index] }
    product.quantity++;

    const products = [...this.state.productData]
    products[index] = product

    this.setState({ productData: products })
  }

  decrementHandler = (id) => {

    const index = this.state.productData.findIndex(p => p.id === id)

    const product = { ...this.state.productData[index] }

    if (product.quantity > 1) {
      product.quantity--;

      const products = [...this.state.productData]
      products[index] = product

      this.setState({ productData: products })
    }
  }

  render() {
    return (
      <ProductState.Provider value={this.state.productData}>
        <div class="container mx-auto bg-slate-200" >
          <Navbar />
          <div className="flex flex-col items-center justify-center gap-y-4 p-4">
            <h1 className="font-sans font-bold text-2xl text-slate-800 select-none" >Product List</h1>
            {/*<ClassRef />*/}
            <ProductList
              remove={this.removeHandler}
              increment={this.incrementHandler}
              decrement={this.decrementHandler}
              product={this.state.productData}
            />
          </div >
          <TotalPrice price={this.state.productData.reduce((acc, cur) => acc + (cur.price * cur.quantity), 0)} />
        </div>
      </ProductState.Provider>
    )
  }
}

