import Product from "./components/Product/Product";
import React, { Component, useState, useContext } from "react";
import Counter from "./components/Counter/Counter";
import HooksArray from "./components/Hook/HookArray";
import ProductList from "./components/ProductList/ProductList";
import Navbar from "./components/Navbar/Navbar";
import ClassCounter from "./components/Counter/ClassCounter"
import Wrapper from "./components/hoc/Wrapper";
import ClassRef from "./components/Ref/ClassRef";
import TotalPrice from "./components/TotalPrice/TotalPrice";
import CounterProvider from "./components/context/CounterProvider";

class App extends Component {

  state = {

    productData: [
      { name: 'ساعت هوشمند', price: 150e3, quantity: 1, id: 1 },
      { name: 'ساعت دیواری', price: 100e3, quantity: 1, id: 2 },
      { name: 'کتاب فروشندگی', price: 25e3, quantity: 1, id: 3 },
    ]

  }

  removeHandler = (id) => {
    const productFilter = this.state.productData.filter(p => p.id != id)
    this.setState({ productData: productFilter })
  }

  incrementHandler = (id) => {
    const index = this.state.productData.findIndex(product => product.id === id)

    const productTarget = { ...this.state.productData[index] }
    productTarget.quantity++;

    const allProduct = [...this.state.productData]
    allProduct[index] = productTarget;

    this.setState({ productData: allProduct })
  }

  decrementHandler = (id) => {
    const index = this.state.productData.findIndex(product => product.id == id)
    const productTarget = { ...this.state.productData[index] }
    if (productTarget.quantity > 1) {
      productTarget.quantity--;

      const allProduct = [...this.state.productData]
      allProduct[index] = productTarget;

      this.setState({ productData: allProduct })
    }
  }

  render() {
    return (
      <div class="container mx-auto bg-slate-200" >
        <Navbar price={this.state.productData.length} />
        <Counter />
        <div className="flex flex-col items-center justify-center gap-y-4 p-4">
          <h1 className="font-sans font-bold text-2xl text-slate-800 select-none" >Product List</h1>
          <ProductList
            remove={this.removeHandler}
            increment={this.incrementHandler}
            decrement={this.decrementHandler}
            product={this.state.productData}
          />
        </div >
        <TotalPrice price={this.state.productData.reduce((acc, cur) => acc + (cur.price * cur.quantity), 0)} />
      </div>
    )
  }
}

