import { useState } from "react";
import Todo from "./Todo/Todo";
import TodoForm from "./TodoForm/TodoForm";
import TodoList from "./TodoList/TodoList";

const TodoApp = () => {
  const [todo, setTodo] = useState([])

  const addToTodo = (input) => {
    const myDate = new Date()
    const fullYear = {
      year: myDate.getFullYear(),
      month: myDate.getMonth() + 1,
      day: myDate.getDate(),
      hours: myDate.getHours(),
      minute: myDate.getMinutes()
    }
    setTodo([...todo, {
      text: input,
      id: Math.floor(Math.random() * 100),
      inYear: fullYear,
      isCheck: false
    }])
  }

  const removeHandler = (id) => {
    const filterTodo = todo.filter(todo => todo.id !== id)
    setTodo(filterTodo)
  }

  const checkHandler = (id) => {
    const index = todo.findIndex(todo => todo.id === id)

    const itemTodo = { ...todo[index] }
    console.log(id)
    //itemTodo.isCheck = !itemTodo.isCheck;

    //const allTodo = [...todo]
    //allTodo[index] = itemTodo;

    //setTodo(allTodo)
  }

  return (
    <div className="w-full">
      <div className="container p-4">
        <TodoForm addToTodo={addToTodo} />
        <TodoList
          todo={todo}
          remove={removeHandler}
          check={checkHandler}
        />
      </div>
    </div>
  );
}

export default TodoApp;
