import { useRef } from 'react';
import { HiChevronRight } from 'react-icons/hi'
import { HiOutlineSearch } from 'react-icons/hi'
import { HiLogin } from 'react-icons/hi'
import { HiMoon } from 'react-icons/hi'

const Header = () => {
  const navbar = useRef()

  //const showNavbar = () => {
  //  if (navbar.current.matches('.right-full')) {
  //    navbar.current.classList.remove('right-full')
  //    navbar.current.classList.add('right-0')
  //  } else {
  //    navbar.current.classList.remove('right-0')
  //    navbar.current.classList.add('right-full')
  //  }
  //}

  //const closeNavbar = () => {
  //  if (navbar.current.matches('.right-0')) {
  //    navbar.current.classList.remove('right-0')
  //    navbar.current.classList.add('right-full')
  //  } else {
  //    navbar.current.classList.remove('right-full')
  //    navbar.current.classList.add('right-0')
  //  }
  //}

  return (
    <nav class='flex flex-col lg:h-full lg:w-60 bg-white lg:p-2 fixed top-0 left-0'>
      <header className='lg:w-full lg:h-14 relative flex items-center justify-start'>
        <div>
          <span className='w-11'>
            <img src='/svgLogo/fh-logo.svg' alt='logo' className='bg-gray-100 p-1 rounded-md' ></img>
          </span>
        </div>

        <div className='flex flex-col font-sans ml-2 leading-5'>
          <span className='select-none font-semibold'>community</span>
          <span className='select-none'>digiStyle</span>
        </div>

        <HiChevronRight className='cursor-pointer absolute top-1/3 -right-5 w-6 h-6 rounded-full bg-purple-500 text-white' />
      </header>

      <div className='w-full h-full flex flex-col justify-between'>


        <ul>
          <li className='h-12 px-2 bg-gray-200 list-none w-full flex items-center justify-start rounded-md'>
            <HiOutlineSearch className='text-2xl text-gray-500' />
            <input type='search' className='outline-none bg-gray-200 w-full pl-2' placeholder='search... ' />
          </li>
        </ul>

        <ul>
          <li className='flex items-center justify-between h-12 px-2 bg-white hover:bg-gray-200 list-none w-full rounded-md'>
            <HiMoon className='text-2xl text-gray-500' />
            <span className='ml-2 dark:text-white'>darkMode</span>
            <div className='w-10 h-6 bg-gray-500 rounded-xl relative right-0 cursor-pointer before:'></div>
          </li>

          <li className='h-12 px-2 bg-white hover:bg-gray-200 list-none w-full rounded-md'>
            <a href='/Login' className='w-full h-full flex items-center justify-start'>
              <HiLogin className='text-2xl text-gray-500' />
              <span className='ml-2 dark:text-white'>Login</span>
            </a>
          </li>

        </ul>


      </div>
    </nav>
  );

}

export default Header;
