import Product from "../Product/Product";
import { useState } from 'react';
import { HiExclamationCircle } from "react-icons/hi";
import { useProduct, useProductAction } from "../../app";



const ProductList = (props) => {

  const productData = useProduct()
  const { incrementHandler, decrementHandler, removeHandler } = useProductAction()

  return (
    <div id="ProductList" className="w-full">
      {!product.length &&
        <div className="select-none font-sans text-lg text-slate-800 flex items-center justify-center">
          <HiExclamationCircle className="ml-2 w-6 h-6 fill-amber-400" />
          محصولی در سبد خرید شما وجود ندارد
        </div>}
      {productData.map(product => {
        return <Product
          key={product.id}
          product={product}
          onDelete={() => removeHandler(product.id)}
          increment={() => incrementHandler(product.id)}
          decrement={() => decrementHandler(product.id)}
        />
      })}
    </div>
  );
}

export default ProductList;
