import { useRef } from 'react';
import { BiSearch } from 'react-icons/bi'
import { FiMenu } from 'react-icons/fi'
import { FiLogIn } from 'react-icons/fi'
import { FiHome } from 'react-icons/fi'
import { FiBox } from 'react-icons/fi'
import { FiInfo } from 'react-icons/fi'
import { FiShoppingCart } from 'react-icons/fi'

import Navbar from '../Navbar/Navbar';

const Header = () => {
  //const miniNav = useRef()

  //const onClick = (m) => {
  //  if (miniNav.current.matches('.hidden')) {
  //    miniNav.current.classList.remove('hidden')
  //  } else {
  //    miniNav.current.classList.add('hidden')
  //  }
  //}

  return (
    <div className='dark:bg-gray-900 z-10 w-screen bg-white p-2 shadow-md absolute right-0 top-0 flex flex-row-reverse justify-between lg:justify-center items-center'>

      {window.innerWidth < 769 && <FiMenu className='dark:text-gray-300 text-3xl cursor-pointer inline-block ' />}

      <label className='lg:w-1/3 flex w-auto align-center border-solid border-2 rounded-md items-center justify-start px-2 py-1 '>
        <BiSearch className='text-lg text-gray-300' />
        <input placeholder='...جستوجو کنید' type='text' className='dark:bg-gray-900 dark:text-gray-300 px-2 text-right outline-none w-full' />
      </label>

      {/*{window.innerWidth < 769 && <img className='' src='/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg' alt='noLoaded' />}*/}
      {window.innerWidth < 769 &&
        <div className=' w-full p-2 absolute top-full right-0' >
          <ul className='w-full flex flex-col justify-end'>
            <li className='hover:bg-slate-100 hover:text-sky-500 px-4 py-2'><a className='w-full block' href='/'>Home</a></li>
            <li className='hover:bg-slate-100 hover:text-sky-500 px-4 py-2'><a className='w-full block' href='/Product'>Product</a></li>
            <li className='hover:bg-slate-100 hover:text-sky-500 px-4 py-2'><a className='w-full block' href='/Cart'>Cart</a></li>
            <li className='hover:bg-slate-100 hover:text-sky-500 px-4 py-2'><a className='w-full block' href='/Info'>Info</a></li>
          </ul>
        </div>
      }
    </div>
  );

}

export default Header;
