import { useContext } from "react";
import { ProductDispatcher } from "../context/ProductsProvider";
const Filter = () => {
  const dispatch = useContext(ProductDispatcher)

  return (
    <div className="w-full flex items-center justify-start px-4 py-2">
      <p className="text-md font-sans text-slate-800">فیلتر بر اساس سایز</p>
      <select className="bg-white p-2 mr-2 rounded-lg" onChange={(e) => dispatch({ type: "filterSize", event: e })}>
        <option value="">All</option>
        <option value="S">S</option>
        <option value="M">M</option>
        <option value="L">L</option>
      </select>
    </div>
  )
}

export default Filter;
