import { useState } from "react";
import { BiFilterAlt } from "react-icons/bi";

const FilterType = ({ setType, type }) => {

  return (
    <div className="w-full bg-white pb-3 pt-1 pl-4 flex items-center justify-end sticky top-[54px]">
      <ul className="w-full flex items-center justify-end overflow-x-auto">
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Activity" && "bg-zinc-800 text-white"}`} >Activity</li>
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Lifestyle" && "bg-zinc-800 text-white"}`} >Lifestyle</li>
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Work" && "bg-zinc-800 text-white"}`} >Work</li>
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "School" && "bg-zinc-800 text-white"}`} >School</li>
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Home" && "bg-zinc-800 text-white"}`} >Home</li>
        <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "All" && "bg-zinc-800 text-white"}`} >All</li>
      </ul>
      <div className="w-9 h-9 flex items-center justify-center mr-2">
        <BiFilterAlt className="text-3xl" />
      </div>
    </div>
  );
}

export default FilterType;
