import { useState } from "react";
import WithCounter from "../hoc/withCounter";

const Counter = ({ count, increment }) => {
  return (
    <div className="flex items-center justify-center">
      <button onClick={increment} className="bg-purple-500 text-white text-lg px-2 flex items-center justify-center rounded hover:ring ring-purple-500 ring-offset-4 ring-offset-purple-200 transition-all duration-300" > + </button>
      <span className="font-sans text-lg mx-2">{count}</span>
      <button onClick={increment} className="bg-purple-500 text-white text-lg px-2 flex items-center justify-center rounded hover:ring ring-purple-500 ring-offset-4 ring-offset-purple-200 transition-all duration-300"> - </button>
    </div>
  );
}

export default WithCounter(Counter);
