const CardProduct = (props) => {
  return (
    <div className='w-48 h-96'>
      <div className='w-full h-3/10'>
        <img src={props.img} alt='noLoaded' className='max-h-full'></img>
      </div>
      <span className='w-full inline-block text-right text-sm'>{props.name}</span>
      <span className=' mt-2 w-full flex items-center justify-end text-sm text-violet-600'><h2 className='text-lg text-gray-900 font-medium ml-1'>{props.price}</h2> تومان</span>
      <button className='border-solid border-2 border-violet-500 text-violet-500 rounded px-8 py-2 mt-4 cursor-pointer text-sm hover:bg-violet-500 hover:text-white '>مشاهده محصول</button>
    </div>
  );
}

export default CardProduct;
