import React, { Component } from 'react';


class ClassRef extends Component {
  //state = {}

  constructor(props) {
    super(props)
    this.inputRef = React.createRef()
  }

  componentDidMount() {
    console.log(this.inputRef)
  }

  render() {
    return (
      <div className='w-full flex items-center justify-center '>
        <input ref={this.inputRef} placeholder="جستجو کنید...." type="text" className='w-1/2 mx-auto p-2 border-2 border-slate-400 ring-offset-2 ring-blue-500 focus:ring-4  outline-none rounded-lg' />
      </div>
    );
  }
}

export default ClassRef;
