import { useContext, createContext, useReducer } from 'react';
import _ from "lodash"
export const ProductContext = createContext()
export const ProductDispatcher = createContext()

const ProductsProvider = ({ children }) => {
  const initialState = [
    { name: 'مانتو', price: 150e3, quantity: 1, id: 1, size: ["XL", "S"] },
    { name: 'کت مردانه', price: 100e3, quantity: 1, id: 2, size: ["L", "M"] },
    { name: 'شلوار جین', price: 25e3, quantity: 1, id: 3, size: ["S", "M"] },
  ]

  const [productData, dispatch] = useReducer((state, action) => {
    switch (action.type) {
      case "increment": {
        const index = state.findIndex(product => product.id === action.id)
        const productTarget = { ...state[index] }
        productTarget.quantity++;
        const allProduct = [...state]
        allProduct[index] = productTarget;
        return allProduct;
      }
      case "decrement": {
        const index = state.findIndex(product => product.id === action.id)
        const productTarget = { ...state[index] }
        productTarget.quantity--;
        const allProduct = [...state]
        allProduct[index] = productTarget;
        return allProduct;
      }
      case "remove": {
        const productFilter = state.filter(p => p.id != action.id)
        return productFilter;
      }
      case "filterSize": {
        if (action.selectOptions.value === "") {
          return initialState;
        } else {
          const filterSizeProduct = initialState.filter(p => p.size.indexOf(action.selectOptions.value) >= 0)
          return filterSizeProduct;
        }
      }
      case "filterPrice": {
        const value = action.selectOptions.value
        const products = [...state]
        if (value === "highest") {
          return _.orderBy(products, ["price"], ["desc"])
        } else {
          return _.orderBy(products, ["price"], ["asc"])
        }
      }
      default:
        return state;
    }
  }, initialState)

  return (
    <ProductContext.Provider value={productData}>
      <ProductDispatcher.Provider value={dispatch}>
        {children}
      </ProductDispatcher.Provider>
    </ProductContext.Provider>
  )
}

export default ProductsProvider;
