import { useRef } from 'react';
import { BiSearch } from 'react-icons/bi'
import { FiMenu } from 'react-icons/fi'
import { FiLogIn } from 'react-icons/fi'
import { FiHome } from 'react-icons/fi'
import { FiBox } from 'react-icons/fi'
import { FiInfo } from 'react-icons/fi'
import { FiShoppingCart } from 'react-icons/fi'
import { FiX } from 'react-icons/fi'

import Navbar from '../Navbar/Navbar';

const Header = () => {
  const navbar = useRef()

  const showNavbar = () => {
    if (navbar.current.matches('.right-full')) {
      navbar.current.classList.remove('right-full')
      navbar.current.classList.add('right-0')
    } else {
      navbar.current.classList.remove('right-0')
      navbar.current.classList.add('right-full')
    }
  }

  const closeNavbar = () => {
    if (navbar.current.matches('.right-0')) {
      navbar.current.classList.remove('right-0')
      navbar.current.classList.add('right-full')
    } else {
      navbar.current.classList.remove('right-full')
      navbar.current.classList.add('right-0')
    }
  }

  return (
    <div className='dark:bg-zinc-900 z-10 w-screen bg-white p-2 shadow-md absolute right-0 top-0 flex flex-row justify-between lg:justify-center items-center'>

      {window.innerWidth < 769 && <FiMenu onClick={showNavbar} className='dark:text-gray-100 text-3xl cursor-pointer inline-block ' />}

      <label className='lg:w-1/3 flex w-auto align-center border-solid border-2 dark:border-gray-500 rounded-md items-center justify-start px-2 py-1 '>
        <BiSearch className='text-lg text-gray-400 dark:text-gray-200 ' />
        <input placeholder='...جستوجو کنید' type='text' className='dark:bg-zinc-900 dark:text-gray-200 px-2 text-right outline-none w-full' />
      </label>

      {/*{window.innerWidth < 769 && <img className='' src='/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg' alt='noLoaded' />}*/}

      <div ref={navbar} className=' w-full lg:w-56 h-screen p-2 absolute top-0 right-full lg:left-0 lg:top-full bg-white dark:bg-zinc-800 duration-500' >
        {window.innerWidth < 769 && <FiX onClick={closeNavbar} className='dark:text-white text-3xl cursor-pointer dark:hover:bg-gray-600 rounded ml-2 my-4' />}
        <ul className='w-full flex flex-col items-start dark:text-gray-300 '>
          <li className='w-full hover:bg-slate-100 dark:hover:bg-gray-600 rounded px-4 py-2'><a className='w-full text-lg flex items-center' href='/'><FiHome className='mr-2 dark:text-gray-300' />Home</a></li>
          <li className='w-full hover:bg-slate-100 dark:hover:bg-gray-600 rounded px-4 py-2'><a className='w-full text-lg flex items-center' href='/Product'><FiBox className='mr-2 dark:text-gray-300' />Product</a></li>
          <li className='w-full hover:bg-slate-100 dark:hover:bg-gray-600 rounded px-4 py-2'><a className='w-full text-lg flex items-center' href='/Cart'><FiShoppingCart className='mr-2 dark:text-gray-300' />Cart</a></li>
          <li className='w-full hover:bg-slate-100 dark:hover:bg-gray-600 rounded px-4 py-2'><a className='w-full text-lg flex items-center' href='/Info'><FiInfo className='mr-2 dark:text-gray-300' />Info</a></li>
        </ul>
      </div>

    </div>
  );

}

export default Header;
