import { useState } from "react";
import WithCounter from "../hoc/withCounter";
import { useCount, useCountAction } from "../context/CounterProvider";
const Counter = () => {
  const count = useCount()
  const setCount = useCountAction()
  return (
    <div className="flex items-center justify-center">
      <button onClick={() => setCount(count + 1)} className="bg-purple-500 text-white text-lg px-2 flex items-center justify-center rounded hover:ring ring-purple-500 ring-offset-4 ring-offset-purple-200 transition-all duration-300" > + </button>
      <span className="font-sans text-lg mx-2">{count}</span>
      <button onClick={count < 0 ? () => setCount(count - 1) : ''} className="bg-purple-500 text-white text-lg px-2 flex items-center justify-center rounded hover:ring ring-purple-500 ring-offset-4 ring-offset-purple-200 transition-all duration-300"> - </button>
    </div >
  );
}

export default WithCounter(Counter);
