import React, { createContext, useContext, useReducer, useState } from 'react';

const Count = createContext()
const CountDispatcher = createContext()

const CounterProvider = ({ children }) => {
  let initialState = 0
  const [count, dispatch] = useReducer((state, action) => {
    switch (action) {
      case "increment":
        return state + 1;
      case "reset":
        return initialState;
      case "decrement":
        return state > 1 ? state - 1 : 0
    }
  }, initialState)

  return (
    <Count.Provider value={count} >
      <CountDispatcher.Provider value={dispatch} >
        {children}
      </CountDispatcher.Provider>
    </Count.Provider>
  );
}

export default CounterProvider;

export const useCount = () => useContext(Count)
export const useCountAction = () => useContext(CountDispatcher)
