import { FiEdit2 } from "react-icons/fi";
import { FiTrash } from "react-icons/fi";
import { AiOutlineCalendar } from "react-icons/ai";
import { ImCheckboxUnchecked } from "react-icons/im";
import { ImCheckboxChecked } from "react-icons/im";

const Todo = ({ todo, remove, checkHandler }) => {
  return (
    <div className={`w-full mt-2 bg-white border shadow rounded-lg flex flex-col items-center justify-center p-3 ${todo.isCheck && "line-through opacity-40"}`} >
      <div className="flex w-full">
        <div className="w-[10%] flex flex-col justify-start items-center">
          <div className="w-7 h-7 bg-blue-500 flex items-center justify-center rounded-md">
            <FiEdit2 className="cursor-pointer text-white" />
          </div>
          <div className="cursor-pointer w-7 h-7 mt-2 bg-red-500 flex items-center justify-center rounded-md">
            <FiTrash onClick={() => remove(todo.id)} className=" text-white" />
          </div>
          <div className="w-7 h-7 mt-2 flex items-center justify-center rounded-md overflow-hidden">
            {todo.isCheck ? <ImCheckboxChecked onClick={() => checkHandler(todo.id)} className="cursor-pointer bg-white fill-slate-700 w-full h-full" /> : <ImCheckboxUnchecked onClick={() => checkHandler(todo.id)} className="cursor-pointer text-slate-700 w-full h-full" />}
          </div>
        </div>
        <div className="w-[90%] flex flex-col">
          <div className="w-full flex justify-end overflow-x-scroll">
            <span className="border border-slate-400 px-2 rounded-full select-none text-md font-sans mr-2">School</span>
            <span className="border border-slate-400 px-2 rounded-full select-none text-md font-sans mr-2">Everyday</span>
            <span className="border border-slate-400 px-2 rounded-full select-none text-md font-sans mr-2">Works</span>
            {/*<span className="border border-gray-500 px-2 rounded-full select-none text-md font-sans mr-2">Activity</span>
          <span className="border border-gray-500 px-2 rounded-full select-none text-md font-sans mr-2">Lifestyle</span>
          <span className="border border-gray-500 px-2 rounded-full select-none text-md font-sans mr-2">Home</span>*/}
          </div>
          <div className="flex flex-col items-end overflow-hidden w-full">
            <p className="select-none w-full truncate text-lg my-4 text-left">{todo.text}</p>
            <span className="text-sans text-sm flex items-center text-slate-800 select-none">
              {todo.date}
              <AiOutlineCalendar className="mr-2 w-5 h-5" />
            </span>
            <span className="text-sans text-sm flex items-center text-slate-800 select-none">
              {todo.time}
              <AiOutlineCalendar className="mr-2 w-5 h-5" />
            </span>
          </div>
        </div>
      </div>
    </div >
  );
}

export default Todo;
