import { useRef } from 'react';
import { FiMenu } from 'react-icons/fi'
import { FiLogIn } from 'react-icons/fi'
import { BiSearch } from 'react-icons/bi'
import { FiHome } from 'react-icons/fi'
import { FiBox } from 'react-icons/fi'

const Navbar = () => {
  const miniNav = useRef()

  const onClick = (m) => {
    if (miniNav.current.matches('.hidden')) {
      miniNav.current.classList.remove('hidden')
    } else {
      miniNav.current.classList.add('hidden')
    }
  }

  return (
    <header className=' bg-white container mx-auto border-solid border-gray-200 border-b-2 px-2 sticky inset-0 flex flex-row-reverse items-center justify-between sm:p-2 '>

      {window.innerWidth < 769 && <FiMenu onClick={onClick} className=' text-3xl cursor-pointer inline-block ' />}

      {window.innerWidth < 640 && <img className='' src="../../../public/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg" alt="!loaded" />}
      {window.innerWidth > 1023 && <img className='' src="../../../public/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg" alt="!loaded" />}

      {window.innerWidth > 640 && <div className='flex align-center sm:relative'><input type='text' className='sm:border-solid border-2 sm:rounded-md sm:px-7 sm:py-1 sm:outline-none focus:border-sky-500' /><BiSearch className='absolute left-2 top-1/4 text-lg text-gray-400' /></div>}

      <nav ref={miniNav} className='hidden w-full md:w-auto md:border-none md:h-full md:block md:relative absolute  bg-white top-full right-0 overflow-hidden border-solid border-b-2 border-gray-200'>
        <ul className='divide-y divide-dashed md:divide-none text-md md:flex md:justify-end md:p-1'>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-2 w-full h-full flex justify-end items-center'><FiHome className='text-md mr-2' />home</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-2 w-full h-full flex justify-end items-center'><FiBox className='text-md mr-2' />product</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-2 w-full h-full flex justify-end items-center'>cart</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-2 w-full h-full flex justify-end items-center'>info</a></li>
        </ul>
      </nav>

      <span className='h-full flex items-center text-md font-sans py-3 cursor-pointer '>
        login<FiLogIn className='text-xl ml-1' />
      </span>

    </header>
  );
}

export default Navbar;
