import { useState } from "react";

const FilterType = () => {
  const [filterType, setFilterType] = useState('All')
  return (
    <div className="w-full bg-white pb-3 pl-4">
      <div>

      </div>
      <ul className="w-full flex items-center justify-end overflow-x-auto">
        <li onClick={(e) => setFilterType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white text-slate-800 py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${filterType === "Home" && "bg-zinc-800 text-white"}`} >Home</li>
        <li onClick={(e) => setFilterType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white text-slate-800 py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${filterType === "" && "bg-zinc-800 text-white"}`} >All</li>
      </ul>
    </div>
  );
}

export default FilterType;
