const CardProduct = (props) => {
  return (
    <div className='w-48 h-80'>
      <div className='w-full h-3/10'>
        <img src={props.img} alt='noLoaded' className='max-h-full'></img>
      </div>
      <span className='w-full inline-block text-right text-sm'>{props.name}</span>
      <span className=' mt-2 w-full flex items-center justify-end text-sm text-red-300'><h2 className='text-lg text-gray-900 font-medium ml-1'>{props.price}</h2> تومان</span>
    </div>
  );
}

export default CardProduct;
