import { useContext } from "react";
import { ProductContext } from "../context/ProductsProvider";


const TotalPrice = () => {

  const products = useContext(ProductContext)

  return (
    <div class="flex justify-between items-center bg-white w-full lg:hidden fixed bottom-0 ring-0 py-4 px-3">
      <button class="font-sans text-md text-white bg-orange-400 rounded-lg py-3 px-5">ادامه فرایند</button>
      <span class="font-sans font-bold text-sm text-orange-400">
        <span class="inline-block font-sans font-bold text-lg text-orange-400"> قیمت کل : </span>
        {products.length} تومان
      </span>
    </div>
  );
}

export default TotalPrice;
