import { useContext, useState } from "react";
import { ProductDispatcher } from "../context/ProductsProvider";
import Select from "react-select"



const Filter = () => {

  const [valueSize, setValueSize] = useState({ value: '', label: 'ALL' })
  const [valuePrice, setValuePrice] = useState({ value: 'highest', label: 'قیمت کم به زیاد' })
  const dispatch = useContext(ProductDispatcher)

  const optionsSize = [
    { value: '', label: 'All' },
    { value: 'M', label: 'M' },
    { value: 'L', label: 'L' },
    { value: 'S', label: 'S' },
    { value: 'XL', label: 'XL' }
  ]

  const optionsPrice = [
    { value: 'highest', label: 'قیمت کم به زیاد' },
    { value: 'lowest', label: 'قیمت زیاد به کم' },
  ]

  const selectSizeHandler = (selectOptions) => {
    setValueSize(selectOptions)
    dispatch({ type: "filterSize", selectOptions })
    dispatch({ type: "filterPrice", selectOptions: valuePrice })
  }

  const selectPriceHandler = (selectOptions) => {
    setValuePrice(selectOptions)
    dispatch({ type: "filterPrice", selectOptions })
  }

  return (
    <div className="w-full flex items-center justify-start px-4 py-2 bg-white flex-wrap">
      <div className="flex mt-2">
        <p className="text-sm md:text-lg font-sans text-slate-800">جستوجو :</p>
        <input placeholder="search..." className="text-md font-sans border px-4 py-2 rounded-lg" />
      </div>
      <div className="flex mt-2">
        <p className="text-sm md:text-lg font-sans text-slate-800">فیلتر بر اساس سایز :</p>
        <Select className="mx-4 max-w-[150px] min-w-[70px]" value={valueSize} onChange={(e) => selectSizeHandler(e)} options={optionsSize} />
      </div>
      <div className="flex mt-2">
        <p className="text-sm md:text-lg font-sans text-slate-800">فیلتر بر اساس قیمت :</p>
        <Select className="mr-4 max-w-[150px] min-w-[70px]" value={valuePrice} onChange={(e) => selectPriceHandler(e)} options={optionsPrice} />
      </div>
    </div>
  )
}

export default Filter;
