import { useRef } from 'react';
import { FiMenu } from 'react-icons/fi'
import { FiLogIn } from 'react-icons/fi'

const Navbar = () => {
  const miniNav=useRef()

  const onClick = (m)=>{
    if(miniNav.current.matches('.hidden')){
      miniNav.current.classList.remove('hidden')
    }else{
      miniNav.current.classList.add('hidden')
    }
  }
  
  return (
    <header className=' bg-white container mx-auto border-solid border-gray-200 border-b-2 px-2 sticky inset-0 flex flex-row-reverse items-center justify-between sm:p-2 '>
      {window.innerWidth < 640 && <FiMenu onClick={onClick} className=' text-3xl cursor-pointer inline-block ' />}
      <img className='' src="../../../public/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg" alt="!loaded" />
      {window.innerWidth > 640 && <input type='tetxt' className='sm:border-solid border-2 sm:rounded-md sm:px-4 sm:py-1 sm:outline-none focus:border-sky-500'/>}
      <span className='h-full flex items-center text-md font-sans py-3 cursor-pointer ' >
        login<FiLogIn className='text-xl ml-1'/>
      </span>
      <nav ref={miniNav} className='hidden sm:block sm:container absolute w-full bg-white top-full right-0 overflow-hidden border-solid border-b-2 border-gray-200'>
        <ul className='text-md sm:flex sm:justify-end sm:p-1'>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-3 inline-block w-full h-full'>home</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-3 inline-block w-full h-full'>product</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-3 inline-block w-full h-full'>info</a></li>
          <li className='text-right hover:bg-sky-500 hover:text-white sm:hover:rounded sm:hover:bg-sky-500 sm:ml-2 '><a href="#" className='p-2 sm:px-4 sm:py-3 inline-block w-full h-full'>cart</a></li>
        </ul>
      </nav>
    </header>
  );
}

export default Navbar;
