import { useContext, useState } from "react";
import { ProductDispatcher } from "../context/ProductsProvider";
import Select from "react-select"



const Filter = () => {

  const [value, setValue] = useState({ type: '', label: 'ALL' })
  const dispatch = useContext(ProductDispatcher)

  const options = [
    { type: '', label: 'All' },
    { type: 'M', label: 'M' },
    { type: 'L', label: 'L' },
    { type: 'S', label: 'S' },
    { type: 'XL', label: 'XL' }
  ]


  const selectSizeHandler = (selectOptions) => {
    setValue(selectOptions)
    dispatch({ type: "filterSize", selectOptions })
  }

  return (
    <div className="w-full flex items-center justify-start px-4 py-2 bg-white">
      <p className="text-md font-sans text-slate-800">فیلتر بر اساس سایز :</p>
      <Select className="mr-4" value={value} onChange={(e) => selectSizeHandler(e)} options={options} />
    </div>
  )
}

export default Filter;
