import { useContext, createContext, useReducer } from 'react';

const ProductContext = createContext()
const ProductDispatcher = createContext()

const initialState = [
  { name: 'ساعت هوشمند', price: 150e3, quantity: 1, id: 1 },
  { name: 'ساعت دیواری', price: 100e3, quantity: 1, id: 2 },
  { name: 'کتاب فروشندگی', price: 25e3, quantity: 1, id: 3 },
]

const reducer = (state, action) => {
  switch (action.type) {
    case "increment":
      const index = state.findIndex(product => product.id === action.id)

      const productTarget = [...state[index]]
      productTarget.quantity++;

      const allProduct = [productData]
      allProduct[index] = productTarget;
      return allProduct;
    case "decrement":
      const index2 = state.findIndex(product => product.id == action.id)
      const productTarget2 = [...state[index2]]
      if (productTarget2.quantity > 1) {
        productTarget2.quantity--;
        const allProduct = [productData]
        allProduct[index] = productTarget2;
      }
      return allProduct;
    case "remove":
      const productFilter = state.filter(p => p.id != action.id)
      return productFilter;
    default:
      return state;
  }
}

const ProductsProvider = ({ children }) => {

  const [productData, dispatch] = useReducer(reducer, initialState)

  return (
    <ProductContext.Provider value={productData}>
      <ProductDispatcher.Provider value={dispatch}>
        {children}
      </ProductDispatcher.Provider>
    </ProductContext.Provider>
  )
}

export default ProductsProvider;

export const useProducts = () => useContext(ProductContext)
export const useProductsDispatch = () => useContext(ProductDispatcher)
