import Product from "./components/Product/Product";
import React, { Component } from "react";

class App extends Component {
  state = {
    productData: [
      { name: 'ساعت هوشمند', price: '2.500.000' },
      { name: 'کتاب فروشندگی', price: '500.000' },
    ]
  }

  clickHandler() {
    console.log('click')
  }

  render() {
    return (
      <div className="flex flex-col items-center justify-center gap-y-4 p-4">
        <h1 className="font-sans font-bold text-2xl text-slate-800" >Product List</h1>
        <Product name='دیجیکالا' price="250.000.000" />
        <Product name='ساعت اپل' price="20.000.000" />
        <button onClick={this.clickHandler} className="bg-purple-500 py-2 px-8 rounded-md">click</button>
      </div >
    )
  }
}

