import Product from "../Product/Product";
import { useState, useContext } from 'react';
import { HiExclamationCircle } from "react-icons/hi";
import { ProductContext } from "../../app";
import { useProducts, useProductsDispatch } from "./components/context/ProductsProvider";

const ProductList = ({ remove, increment, decrement, product }) => {

  const products = useProducts()
  const dispatch = useProductsDispatch()

  return (
    <div id="ProductList" className="w-full">
      {!product.length &&
        <div className="select-none font-sans text-lg text-slate-800 flex items-center justify-center">
          <HiExclamationCircle className="ml-2 w-6 h-6 fill-amber-400" />
          محصولی در سبد خرید شما وجود ندارد
        </div>
      }
      {product.map(product => {
        return <Product
          key={product.id}
          product={product}
          onDelete={() => remove({ type: "remove", id: products.id })}
          increment={() => increment(product.id)}
          decrement={() => decrement(product.id)}
        />
      })}
    </div>
  );
}

export default ProductList;
