import { useRef } from 'react';
import { FiMenu } from 'react-icons/fi'
import { FiLogIn } from 'react-icons/fi'

const Navbar = () => {
  const click=useRef()

  const onClick = ()=>{
    click.current.classList.toggle('flex')
  }
  
  return (
    <header className=' bg-white container border-solid border-gray-200 border-b-2 px-2 sm:p-4 sticky inset-0 flex flex-row-reverse items-center justify-between '>
      {window.innerWidth < 640 && <FiMenu onClick={onClick} className=' text-3xl cursor-pointer inline-block ' />}
      <img className='' src="../../../public/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg" alt="!loaded" />
      <span className='h-full flex items-center text-md font-sans py-3 cursor-pointer ' >
        login<FiLogIn className='text-xl ml-1'/>
      </span>
      <nav ref={click} className='flex-col absolute w-full bg-white top-full right-0 overflow-hidden'>
        <ul className='text-md'>
          <li className='p-2 text-right hover:bg-gray-100'><a href="#" className='inline-block w-full'>home</a></li>
          <li className='p-2 text-right hover:bg-gray-100'><a href="#" className='inline-block w-full'>product</a></li>
          <li className='p-2 text-right hover:bg-gray-100'><a href="#" className='inline-block w-full'>info</a></li>
          <li className='p-2 text-right hover:bg-gray-100'><a href="#" className='inline-block w-full'>cart</a></li>
        </ul>
      </nav>
    </header>
  );
}

export default Navbar;
