import { useState } from "react"

const TodoForm = ({ addToTodo }) => {
  const [input, setInput] = useState("")

  const changeHandler = (e) => {
    setInput(e.target.value)
  }

  const submitHandler = (e) => {
    e.preventDefault();

    if (!input) {
      alert('یک متن را وارد کنید')
      return;
    }

    addToTodo(input)
    setInput('')
  }

  return (
    <div className="w-[100vw] h-[100vh] p-4 absolute top-0 right-0 bg-gray-100 ">
      <form onSubmit={submitHandler}>
        <input value={input} onChange={changeHandler} placeholder="text" className="outline-none border-b-2 border-gray-300 focus:border-blue-500 w-full py-2 px-4" />
        <div className="flex flex-col items-end py-4 border-t-2 w-full">
          <span className="font-sans text-slate-700 text-md mt-4">Task Type</span>
          <div className=""></div>
        </div>
        <button type="submit" className="bg-purple-500 mt-4 text-white rounded-md py-2 px-4 ">اضافه کردن</button>
      </form>
    </div>
  );
}

export default TodoForm;
