import { useState } from "react";
import { BiFilterAlt } from "react-icons/bi";
import { useTranslation } from "react-i18next";

const FilterType = ({ setType, type }) => {

  const [t, i18n] = useTranslation()
  return (
    <div className="w-full bg-white sticky top-[54px]">
      <div className="w-full container pb-3 pt-1 pr-4 flex items-center justify-end">
        <div className="w-9 h-9 flex items-center justify-center ">
          <BiFilterAlt className="text-2xl" />
        </div>
        <ul className="w-full flex items-center justify-start overflow-x-scroll">
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "All" && "bg-zinc-800 text-white"}`} value="All" >{t('All')}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Home" && "bg-zinc-800 text-white"}`} value="Home" >{t("Home")}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "School" && "bg-zinc-800 text-white"}`} value="School" >{t("School")}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "EveryDay" && "bg-zinc-800 text-white"}`} value="EveryDay" >{t("EveryDay")}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Work" && "bg-zinc-800 text-white"}`} value="Work" >{t("Work")}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Lifestyle" && "bg-zinc-800 text-white"}`} value="Lifestyle" >{t("Lifestyle")}</li>
          <li onClick={(e) => setType(e.target.value)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === "Activity" && "bg-zinc-800 text-white"}`} value="Activity" >{t("Activity")}</li>
        </ul>
      </div>
    </div>
  );
}

export default FilterType;
