import Product from "./components/Product/Product";
import React, { Component } from "react";

class App extends Component {
  state = {
    productData: [
      { name: 'ساعت هوشمند', price: '2.500.000' },
      { name: 'کتاب فروشندگی', price: '500.000' },
    ]
  }

  clickHandler() {
    console.log('click')
  }

  render() {
    return (
      <div className="flex flex-col items-center justify-center gap-y-4 p-4">
        <h1 className="font-sans font-bold text-2xl text-slate-800" >Product List</h1>
        {this.state.productData.map(item => {
          return <Product name={item.name} price={item.price} />
        })}
        <button onClick={this.clickHandler} className="bg-purple-500 hover:bg-purple-700 text-white py-2 px-8 rounded-md hover:ring-2 ring-blue-200 ring-offset-4 transition-all duration-200 ">click</button>
      </div >
    )
  }
}

