import { useState } from "react"

const TodoForm = ({ addToTodo }) => {
  const [input, setInput] = useState("")

  const changeHandler = (e) => {
    setInput(e.target.value)
  }

  const submitHandler = (e) => {
    e.preventDefault();

    if (!input) {
      alert('یک متن را وارد کنید')
      return;
    }

    addToTodo(input)
  }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <input onChange={changeHandler} placeholder="text" className="outline-none border-b-2 border-gray-300 focus:border-blue-500 w-full py-2 px-4" />
        <button type="submit" className="bg-purple-500 mt-4 text-white rounded-md py-2 px-4 ">اضافه کردن</button>
      </form>
    </div>
  );
}

export default TodoForm;
