import Product from "../Product/Product";
import { useState } from 'react';


const ProductList = () => {
  const [productData, setProductData] = useState([
    { name: 'ساعت هوشمند', price: '2.500.000', quantity: 1, id: 1 },
    { name: 'کتاب فروشندگی', price: '500.000', quantity: 1, id: 2 },
    { name: 'موبایل اپل', price: '25.500.000', quantity: 1, id: 3 },
  ])

  const removeHandler = (id) => {
    const filteredProduct = productData.filter(p => p.id != id)
    setProductData(filteredProduct)
  }

  const incrementHandler = (id) => {
    const products = [...productData]
    const productTarget = products.find(p => p.id === id)
    console.log(productTarget)
  }

  return (
    <div id="ProductList" className="w-full">
      {productData.map(product => {
        return <Product
          name={product.name}
          price={product.price}
          quantity={product.quantity}
          onDelete={() => removeHandler(product.id)}
          increment={() => incrementHandler(product.id)}
        />
      })}
    </div>
  );
}

export default ProductList;
