import Product from "../Product/Product";
import { useState } from 'react';
import { HiExclamationCircle } from "react-icons/hi";

const ProductList = () => {
  const [productData, setProductData] = useState([
    { name: 'ساعت هوشمند', price: '2.500.000', quantity: 1, id: 1 },
    { name: 'کتاب فروشندگی', price: '500.000', quantity: 1, id: 2 },
    { name: 'موبایل اپل', price: '25.500.000', quantity: 1, id: 3 },
  ])

  const removeHandler = (id) => {
    const filteredProduct = productData.filter(p => p.id != id)
    setProductData(filteredProduct)
  }

  const incrementHandler = (id) => {
    const products = [...productData]
    const productTarget = products.find(p => p.id === id)
    productTarget.quantity++;
    setProductData(products)
  }

  const decrementHandler = (id) => {
    const products = [...productData]
    const productTarget = products.find(p => p.id === id)
    if (productTarget.quantity > 1) {
      productTarget.quantity--;
      setProductData(products)
    }
  }



  return (
    <div id="ProductList" className="w-full">
      {!productData.length && <div className="select-none font-sans text-lg text-slate-800 flex items-center"><HiExclamationCircle className="ml-2 w-4" />محصولی در سبد خرید شما وجود ندارد</div>}
      {productData.map(product => {
        return <Product
          key={product.id}
          product={product}
          onDelete={() => removeHandler(product.id)}
          increment={() => incrementHandler(product.id)}
          decrement={() => decrementHandler(product.id)}
        />
      })}
    </div>
  );
}

export default ProductList;
