import { useState } from "react";
import { BiFilterAlt } from "react-icons/bi";

const FilterType = ({ setType, type }) => {

  return (
    <div className="w-full bg-white sticky top-[54px]">
      <div className="w-full container pb-3 pt-1 pr-4 flex items-center justify-end">
        <div className="w-9 h-9 flex items-center justify-center ">
          <BiFilterAlt className="text-2xl" />
        </div>
        <ul className="w-full flex items-center justify-start overflow-x-scroll">
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("All") && "bg-zinc-800 text-white"}`} >{t('All')}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("Home") && "bg-zinc-800 text-white"}`} >{t("Home")}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("School") && "bg-zinc-800 text-white"}`} >{t("School")}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("EveryDay") && "bg-zinc-800 text-white"}`} >{t("EveryDay")}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("Work") && "bg-zinc-800 text-white"}`} >{t("Work")}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("Lifestyle") && "bg-zinc-800 text-white"}`} >{t("Lifestyle")}</li>
          <li onClick={(e) => setType(e.target.textContent)} className={`font-sans font-lg border border-zinc-800 bg-white py-2 px-4 rounded-full mr-2 select-none cursor-pointer ${type === t("Activity") && "bg-zinc-800 text-white"}`} >{t("Activity")}</li>
        </ul>
      </div>
    </div>
  );
}

export default FilterType;
