import { useRef } from 'react';
import { HiChevronRight } from 'react-icons/hi'
import { HiOutlineSearch } from 'react-icons/hi'
import { HiLogin } from 'react-icons/hi'
import { HiMoon } from 'react-icons/hi'

const Header = () => {
  const navbar = useRef()
  const btnDarkMode = useRef()

  const showNavbar = () => {
    if (navbar.current.matches('.right-full')) {
      navbar.current.classList.remove('right-full')
      navbar.current.classList.add('right-0')
    } else {
      navbar.current.classList.remove('right-0')
      navbar.current.classList.add('right-full')
    }
  }

  const closeNavbar = () => {
    if (navbar.current.matches('.right-0')) {
      navbar.current.classList.remove('right-0')
      navbar.current.classList.add('right-full')
    } else {
      navbar.current.classList.remove('right-full')
      navbar.current.classList.add('right-0')
    }
  }

  const toggleDarkMode = () => {
    document.querySelector('html').classList.toggle('dark')

  }


  return (
    <nav class='overflow-hidden dark:bg-zinc-900 flex flex-col lg:h-full lg:w-60 bg-white lg:p-2 fixed top-0 left-0'>
      <header className='lg:w-56 lg:h-14 relative flex items-center justify-start'>
        <div className='min-w-11'>
          <img src='/svgLogo/fh-logo.svg' alt='logo' className='bg-gray-100 p-1 rounded-md' ></img>
        </div>

        <div className='w-44 flex flex-col font-sans ml-2 leading-5'>
          <span className='select-none dark:text-white font-semibold'>community</span>
          <span className='select-none dark:text-white'>digiStyle</span>
        </div>

        <HiChevronRight className='cursor-pointer absolute top-1/3 -right-5 w-6 h-6 rounded-full bg-purple-500 text-white' />
      </header>

      <div className='w-full h-full flex flex-col justify-between'>


        <ul className='mt-2'>
          <li className='h-12 px-2 bg-gray-200 dark:bg-gray-700 list-none w-full flex items-center justify-start rounded-md'>
            <HiOutlineSearch className='text-3xl text-gray-500' />
            <input type='search' className='outline-none bg-gray-200 dark:bg-gray-700 dark:text-white w-full pl-2' placeholder='search... ' />
          </li>
        </ul>

        <ul>
          <li className='w-56 h-12 px-2 hover:bg-gray-100 dark:hover:bg-gray-800 list-none rounded-md'>
            <a href='/Login' className=' h-full flex items-center justify-start'>
              <HiLogin className='text-3xl text-gray-500' />
              <span className='ml-2 dark:text-white'>Login</span>
            </a>
          </li>
          <li className='w-56 flex items-center justify-between h-12 px-2 hover:bg-gray-200 dark:hover:bg-gray-800 list-none w-full rounded-md'>
            <div className='flex items-center'>
              <HiMoon className='text-3xl text-gray-500' />
              <span className='ml-2 dark:text-white'>darkMode</span>
            </div>
            <div onClick={toggleDarkMode} ref={btnDarkMode} className='after:content-[] before:w-4 before:h-4 before:rounded-full before:absolute before:top-1 before:left-1  before:bg-gray-500 dark:before:bg-gray-900 w-12 h-6 bg-gray-300 rounded-xl relative right-0 cursor-pointer before:'></div>
          </li>
        </ul>


      </div>
    </nav>
  );

}

export default Header;
