      <label className='flex w-auto align-center border-solid border-2 rounded-md items-center justify-start px-2 py-1 '>
        <input placeholder='...جستوجو کنید' type='text' className='px-2 text-right sm:px-7 sm:py-1 outline-none' />
        <BiSearch className='text-lg text-gray-400' />
      </label>

      {window.innerWidth < 769 && <img className='w-1/3' src='/svgLogo/logo-quera-heavy.1-1c1287ee3575.svg' alt='noLoaded' />}

    </div>
  );

}

export default Navbar;
